package Ficheros;

public class ejemplo2tablamultiplicar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// tabla de multiplicar del 3
		
		int cont = 1;
		
		while (cont <= 10) {
			System.out.println("3x "+cont+" = "+(3*cont));
			//avance contador
			cont = cont +1;
		}

	}

}
